import React, { useState } from 'react';
import { Box, Typography, Button, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel } from '@mui/material';
import axios from 'axios';

const questions = {
  слесарь: [
    { id: 1, question: 'Вопрос 1 для слесаря', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 2, question: 'Вопрос 2 для слесаря', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 3, question: 'Вопрос 3 для слесаря', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 4, question: 'Вопрос 4 для слесаря', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
  ],
  делопроизводитель: [
    { id: 5, question: 'Вопрос 5 для делопроизводителя', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 6, question: 'Вопрос 6 для делопроизводителя', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 7, question: 'Вопрос 7 для делопроизводителя', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 8, question: 'Вопрос 8 для делопроизводителя', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
  ],
  механник: [
    { id: 9, question: 'Вопрос 9 для механника', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 10, question: 'Вопрос 10 для механника', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 11, question: 'Вопрос 11 для механника', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
    { id: 12, question: 'Вопрос 12 для механника', options: ['Ответ 1', 'Ответ 2', 'Ответ 3'] },
  ],
};

const TestPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [credentials, setCredentials] = useState(null);
  const position = 'слесарь'; // Замените на фактическую должность пользователя

  const handleAnswerChange = (event) => {
    setAnswers({
      ...answers,
      [questions[position][currentQuestionIndex].id]: event.target.value,
    });
  };

  const handleNextQuestion = async () => {
    if (currentQuestionIndex < questions[position].length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Логика завершения теста
      try {
        const response = await axios.post('/api/users/complete-test', { answers, position });
        setCredentials(response.data);
      } catch (error) {
        console.error('Ошибка при завершении теста:', error);
      }
    }
  };

  return (
    <Box sx={{ maxWidth: 600, mx: 'auto', mt: 4 }}>
      <Typography variant="h4" gutterBottom>Тестирование</Typography>
      {credentials ? (
        <Box>
          <Typography variant="h6">Ваш логин: {credentials.username}</Typography>
          <Typography variant="h6">Ваш пароль: {credentials.password}</Typography>
        </Box>
      ) : (
        <FormControl component="fieldset">
          <FormLabel component="legend">{questions[position][currentQuestionIndex].question}</FormLabel>
          <RadioGroup
            value={answers[questions[position][currentQuestionIndex].id] || ''}
            onChange={handleAnswerChange}
          >
            {questions[position][currentQuestionIndex].options.map((option, index) => (
              <FormControlLabel key={index} value={option} control={<Radio />} label={option} />
            ))}
          </RadioGroup>
          <Button variant="contained" color="primary" sx={{ mt: 2 }} onClick={handleNextQuestion}>
            {currentQuestionIndex < questions[position].length - 1 ? 'Далее' : 'Завершить'}
          </Button>
        </FormControl>
      )}
    </Box>
  );
};

export default TestPage;
