import React, { useState } from 'react';
import { Button, TextField, Box, Typography, MenuItem } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    age: '',
    disability: '',
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/users/register', formData);
      console.log('Логин:', response.data.username);
      console.log('Пароль:', response.data.password);
      navigate('/test');
    } catch (error) {
      console.error('Ошибка при регистрации:', error);
    }
  };

  return (
    <Box sx={{ maxWidth: 400, mx: 'auto', mt: 4 }}>
      <Typography variant="h4" gutterBottom>Регистрация</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="ФИО"
          name="name"
          value={formData.name}
          onChange={handleChange}
          margin="normal"
        />
        <TextField
          fullWidth
          label="Должность"
          name="position"
          value={formData.position}
          onChange={handleChange}
          margin="normal"
          select
        >
          <MenuItem value="слесарь">Слесарь</MenuItem>
          <MenuItem value="механник">Механник</MenuItem>
          <MenuItem value="делопроизводитель">Делопроизводитель</MenuItem>
        </TextField>
        <TextField
          fullWidth
          label="Возраст"
          name="age"
          value={formData.age}
          onChange={handleChange}
          margin="normal"
        />
        <TextField
          fullWidth
          label="Степень ОВЗ"
          name="disability"
          value={formData.disability}
          onChange={handleChange}
          margin="normal"
          select
        >
          <MenuItem value="глухота">Глухота</MenuItem>
          <MenuItem value="слепота">Слепота</MenuItem>
          {/* Добавьте другие варианты */}
        </TextField>
        <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
          Далее
        </Button>
      </form>
    </Box>
  );
};

export default RegisterPage;
